INSERT INTO `wp_posts` VALUES ('788', '0', '2012-08-18 21:02:46', '2012-08-18 19:02:46', 'SI\'.\n\nHAI LETTO BENE.\n\nCERCHIAMO TALENTI EMERGENTI E ARREMBANTI PER LA NUOVA STAGIONE RAZZPUTIN 2012/2013 @ BSPACE.\nLOSCHI FIGURI CHE ABBIANO VOGLIA, O CORAGGIO SE PREFERISCI CHIAMARLO COSI\', DI FAR CONOSCERE ALL\'UNIVERSO INTERO LE PROPRIE ABILITA\' E A COSTO ZERO.\n\nDUNQUE ECCO CHI CI INTERESSA DAVVERO PROMUOVERE, MA ATTENZIONE, VALUTIAMO SOLO KIDS AD ALTO TASSO DI MOTIVAZIONE:\n\n- DJS\n\n- BANDS\n\n- FOTOGRAFI\n\n- DESIGNERS\n\n- STILISTI\n\n- VJS\n\n- SOUND DESIGNERS\n\n- PR\n\n- STREET ARTISTS\n\n- BRAND DI ABBIGLIAMENTO\n\n- BRAND DI ACCESSORI\n\n- MAGAZINES E/O FANZINES\n\n- CASE EDITRICI INDIPENDENTI\n\n- ETICHETTE MUSICALI INDIPENDENTI\n\n\n\nGLI INTERESSATI POSSONO INVIARE TUTTO IL MATERIALE IN POSTA PVT ATTRAVERSO LA PAGINA RAZZPUTIN CREW DI FACEBOOK:\n\nhttp://www.facebook.com/razzputin\n\n\nOPPURE\n\n\nDIRETTAMENTE  ALLA NOSTRA MAIL DI RIFERIMENTO:\n\nrazzputincrew@gmail.com\n\nCHI VERRA\' SELEZIONATO, ENTRO E NON OLTRE IL 30 SETTEMBRE 2012, POTRA\' POI ESPORRE LE PROPRIE CREAZIONI O ESIBIRSI IN UN\'AREA DEDICATA ALL\'INTERNO DI UN QUALSIASI, BELLISSIMO, GIOVEDI\' RAZZPUTIN.\n\nHAI CAPITO TUTTO ALLORA?\n\nSE NECESSITI INVECE DI ULTERIORI INFORMAZIONI FACCI UN FISCHIO E NOI TI ACCOGLIEREMO A BRACCIA APERTE CON CON UNA TAZZA DI THE\'  SULLA TESTA.\n\n\n\n\nRZZPTN CREW - AUT VINCERE AUT MORI\n\n\n\n\n\n', 'RAZZPUTIN CERCA TALENTO-NOVELLA STAGIONE 2012/2013', '', 'publish', 'open', 'open', '', 'razzputin-cerca-talento-novella-stagione-20122013', '', '', '2012-08-18 21:02:49', '2012-08-18 19:02:49', '', '0', 'http://localhost/wordpress_old_css/?p=788', '0', 'ai1ec_event', '', '0');
